{
  "topico": "Fotossíntese",
  "disciplina": "Ciências",
  "nivel": "aprofundamento",
  "descricao": "A fotossíntese é o processo bioquímico pelo qual plantas, algas e algumas bactérias transformam energia luminosa em energia química, armazenada na forma de glicose. Esse processo é fundamental para a manutenção da vida na Terra, pois libera oxigênio e forma a base das cadeias alimentares.",
  "detalhamento": {
    "etapas": [
      "Fase clara: ocorre nos tilacoides e depende da luz solar para gerar ATP e NADPH.",
      "Fase escura (Ciclo de Calvin): ocorre no estroma e utiliza ATP e NADPH para fixar CO₂ em glicose."
    ],
    "equacao_simplificada": "6 CO₂ + 6 H₂O + luz → C₆H₁₂O₆ + 6 O₂",
    "curiosidades": [
      "A fotossíntese influencia diretamente o clima global.",
      "As plantas não 'respiram' oxigênio à noite da mesma forma que nós — elas continuam trocando gases, mas em menor intensidade."
    ],
    "referencias": [
      {
        "titulo": "Fotossíntese - Khan Academy",
        "url": "https://pt.khanacademy.org/science/biology/photosynthesis"
      },
      {
        "titulo": "Biologia Hoje - Capítulo 6",
        "url": "https://www.editorasanar.com.br/biologia-fotossintese"
      }
    ]
  }
}

{
  "topico": "Coordenadas Geográficas (Latitude e Longitude)",
  "disciplina": "Geografia",
  "descricao": "Sistema de paralelos (latitude) e meridianos (longitude) para localizar pontos na superfície terrestre.",
  "detalhamento": {
    "etapas": [
      "1. Identificar latitude (N/S) e longitude (E/O).",
      "2. Ler valores em graus decimais ou DMS (graus, minutos, segundos).",
      "3. Cruzar as linhas no mapa ou globo para localizar o ponto."
    ]
  },
  "referencias": [
