}

{
  "topico": "Mapas (Elementos e Leitura)",
  "disciplina": "Geografia",
  "descricao": "Conceitos básicos de mapas e como interpretar seus elementos (título, legenda, escala, orientação e coordenadas) para localizar e medir.",
  "detalhamento": {
    "etapas": [
      "1. Identificar título, fonte e legenda para entender o tema e os símbolos do mapa.",
      "2. Verificar a orientação (N) e/ou rosa dos ventos; usar coordenadas (latitude/longitude) para localizar pontos.",
      "3. Aplicar a escala (numérica ou gráfica) para converter distâncias do mapa para o terreno."
    ]
  },
  "referencias": [
    { "titulo": "Brasil Escola — Elementos do mapa", "url": "https://brasilescola.uol.com.br/geografia/elementos-do-mapa.htm" },
