  ]
}

{
  "topico": "Climas da Terra",
  "disciplina": "Geografia",
  "descricao": "Os climas da Terra são definidos pela interação entre temperatura, umidade, ventos, latitude, altitude e influência marítima. Eles determinam padrões de tempo e influenciam vegetação, ocupação humana e atividades econômicas.",
  "detalhamento": {
    "etapas": [
      "1. Analisar a latitude: regiões próximas ao Equador tendem a ser mais quentes; polos mais frios.",
      "2. Considerar altitude e relevo: áreas mais altas são mais frias; barreiras montanhosas afetam chuvas.",
      "3. Observar maritimidade e continentalidade: áreas costeiras têm menor variação térmica; interiores são mais extremos.",
      "4. Identificar massas de ar e correntes marítimas: responsáveis por umidade, secura e anomalias climáticas.",
      "5. Classificar o clima segundo padrões globais: tropical, equatorial, temperado, desértico, frio, polar, entre outros."
    ]
  },
  "referencias": [
    { "titulo": "Brasil Escola — Climas do mundo", "url": "https://brasilescola.uol.com.br/geografia/climas-do-mundo.htm" },
    { "titulo": "Toda Matéria — Climas do planeta", "url": "https://www.todamateria.com.br/climas-da-terra/" }
  ]
}

